import React, { useEffect, useState } from "react";
import { useForm } from 'react-hook-form';
import axios from "axios";
import { Button, FloatingLabel, Modal, Form } from "react-bootstrap";
import { useParams } from "react-router-dom";


interface FormData {
  sname: string
  smob: string
  semail: string
  sstandard: number
  sparent1: string
  sparent2: string
}

interface Sprofile {
    id: number
    email: string
    mobile: string
  }

  interface Sstandard {
    id: number
    standardName: string
  }

  interface Sparent {
    id: number
    parentName: string
  }

interface Student {
    id: number
    studentName: string
    profile : Sprofile
    parents:Sparent[] 
    standards : Sstandard[]
  }


const Editstudent = () =>{
    const { sid } = useParams();

const [notesList, setnotesList] = useState<Student>();
const { register, handleSubmit, reset } = useForm<FormData>();

useEffect(() => {
    getStudent();
    }, []);
  const getStudent= async () => {
      try{
          const response = await axios.get(`http://localhost:5000/students/${sid}`)
          const gotdata =  response.data;
          console.log(gotdata);
          setnotesList(gotdata);
          console.log(notesList);
        }catch(err){
          console.error(err);
        }
      };
async function create(data: FormData) {
  const { sname, smob, semail, sstandard,sparent1 ,sparent2} = data;
  const variables = { sname, smob, semail, sstandard,sparent1,sparent2 };
  try{
    await axios.post('http://localhost:5000/students',variables).then(data => {
      console.log(data);
      reset();
    })
  }catch(err){
    console.error(err);
  }

}
return (
  <div>
    <div className="container-fluid"><br></br><br></br>
      <div className="row"><div className="col-md-4"></div>
        <div className="col-md-3">
        <h3>UPDATE STUDENT DETAILS</h3><br></br><br></br>
          <form onSubmit={handleSubmit(create)}>
            <div className="form-group">
              <input type="text"  className={`form-control`} placeholder="Name"  {...register("sname")} />     
            </div><br></br>

            <div className="form-group">
              <input type="text"  className={`form-control`} placeholder="Email"  {...register("semail")}/>     
            </div><br></br>

            <div className="form-group">
              <input type="text"  className={`form-control`} placeholder="Phone no"  {...register("smob")}/>     
            </div><br></br>


            <div className="form-group">
            <input type="text"  className={`form-control`} placeholder="Class"  {...register("sstandard")}/> 
            </div><br></br>

            <div className="form-group">
              <input type="text"  className={`form-control`} placeholder="Father Name"  {...register("sparent1")}/>     
            </div><br></br>

            <div className="form-group">
              <input type="text"  className={`form-control`} placeholder="Mother Name"  {...register("sparent2")}/>     
            </div>

            <br></br>
            <button className="btn btn-primary pl-3 pr-3" type="submit"> Save </button>

          </form>
        </div>
      </div>
    </div>
  </div>
);
}

export default Editstudent;
