import React, { useEffect, useState } from "react";
import { useForm } from 'react-hook-form';
import "./App.css";
import axios from "axios";
//import Note from "./components/Note/Note";
//import INote from "./interfaces/note.interface";
//import { getNotes, createNotes, deleteNotes, updateNotes } from "./services/notesService";
import { Button, FloatingLabel, Modal, Form } from "react-bootstrap";


interface FormData {
  sname: string
  smob: string
  semail: string
  sstandard: string
  sparent1: string
  sparent2: string
}

interface IStandard {
  standardname : string
}

function App() {
  const [standardList, settandardList] = useState<Array<IStandard>>([]);
  const { register, handleSubmit, reset } = useForm<FormData>();

  useEffect(() => {
    getStandardfromserver();
  }, []);
  const getStandardfromserver = async () => {
    const standardfromserver = await axios.get('http://localhost:5000/');
    settandardList(standardfromserver.data);
  };


  async function create(data: FormData) {
    const { sname, smob, semail, sstandard,sparent1 ,sparent2} = data;
    const variables = { sname, smob, semail, sstandard,sparent1,sparent2 };
    try{
      const response = await axios.post('http://localhost:5000/',variables)
      console.log('hit the backend');

    }catch(err){
      console.error(err);
    }

  }
  return (
    <div>
      <div className="notes-list">
        {standardList.map((noteitem, index) => {
          return (
             <p>{index}</p>
          );
        })}
      </div>

      <div className="container-fluid"><br></br><br></br>
        <div className="row"><div className="col-md-4"></div>
          <div className="col-md-3">
          <h3>Add Student</h3><br></br><br></br>
            <form onSubmit={handleSubmit(create)}>
              <div className="form-group">
                <input type="text"  className={`form-control`} placeholder="Name"  {...register("sname")}/>     
              </div><br></br>

              <div className="form-group">
                <input type="text"  className={`form-control`} placeholder="Email"  {...register("semail")}/>     
              </div><br></br>

              <div className="form-group">
                <input type="text"  className={`form-control`} placeholder="Phone no"  {...register("smob")}/>     
              </div><br></br>


              <div className="form-group">
              <select className={`form-control`} {...register("sstandard")}>
                <option value="0">Select</option>
                {standardList.map((standarditem, index) => {
                  return (
                    <option value="{index}">{standarditem.standardname}</option>
                  );
                })}
              </select>     
              </div><br></br>

              <div className="form-group">
                <input type="text"  className={`form-control`} placeholder="Father Name"  {...register("sparent1")}/>     
              </div><br></br>

              <div className="form-group">
                <input type="text"  className={`form-control`} placeholder="Mother Name"  {...register("sparent2")}/>     
              </div>

              <br></br>
              <button className="btn btn-primary pl-3 pr-3" type="submit"> Save </button>

            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
